import{_ as a,o as s,c as e,V as o}from"./chunks/framework.5f0cae6b.js";const u=JSON.parse('{"title":"Configuration","description":"","frontmatter":{},"headers":[],"relativePath":"v1/guide/basic/configuration.md","filePath":"v1/guide/basic/configuration.md"}'),n={name:"v1/guide/basic/configuration.md"},t=o(`<h1 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to &quot;Configuration&quot;">​</a></h1><p>You can get notified whenever there is a block added. This notification can be by email and/or by slack.</p><h2 id="email-notifications" tabindex="-1">Email notifications <a class="header-anchor" href="#email-notifications" aria-label="Permalink to &quot;Email notifications&quot;">​</a></h2><div class="language-php"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_MAIL_ENABLED</span><span style="color:#89DDFF;">=true</span></span>
<span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_MAIL_NAME</span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;">Tripwire</span></span>
<span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_MAIL_FROM</span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;">tripwire</span><span style="color:#89DDFF;">@</span><span style="color:#A6ACCD;">yourdomain</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">com</span></span>
<span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_MAIL_TO</span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;">tripwire</span><span style="color:#89DDFF;">@</span><span style="color:#A6ACCD;">yourdomain</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">com</span></span></code></pre></div><p>If you want to use a different email template for your notifications you can set that up too. Look at <a href="./../customization/notifications.html">customization of notifications</a></p><h2 id="slack-notifications" tabindex="-1">Slack notifications <a class="header-anchor" href="#slack-notifications" aria-label="Permalink to &quot;Slack notifications&quot;">​</a></h2><p>Setup <code>.env</code></p><div class="language-php"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_SLACK_ENABLED</span><span style="color:#89DDFF;">=true</span></span>
<span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_SLACK_FROM</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">Tripwire</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_SLACK_CHANNEL</span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;">you</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">slack</span><span style="color:#89DDFF;">-</span><span style="color:#A6ACCD;">webhook</span></span>
<span class="line"><span style="color:#A6ACCD;">TRIPWIRE_NOTIFICATION_SLACK_EMOJI</span><span style="color:#89DDFF;">=:</span><span style="color:#A6ACCD;">japanese_goblin</span><span style="color:#89DDFF;">:</span></span></code></pre></div><p><a href="./../references/slack-setup.html">create webhook in slack</a></p><h2 id="ready-test" tabindex="-1">Ready &amp; Test <a class="header-anchor" href="#ready-test" aria-label="Permalink to &quot;Ready &amp; Test&quot;">​</a></h2><p>Now you are all set to test and be amazed by the results</p><h3 id="step-1" tabindex="-1">step 1 <a class="header-anchor" href="#step-1" aria-label="Permalink to &quot;step 1&quot;">​</a></h3><p>Go to your app and in whatever field there is fill in the following</p><div class="language-php"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">script </span><span style="color:#82AAFF;">alert</span><span style="color:#89DDFF;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">xss</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">)&gt;</span></span></code></pre></div><p>This will:</p><ol><li>Trigger the tripwire and record the violation</li><li>JSON requests return status 406 with data [ ]</li><li>HTML requests return with status 406 and a nasty block screen</li></ol><h3 id="step-2" tabindex="-1">step 2 <a class="header-anchor" href="#step-2" aria-label="Permalink to &quot;step 2&quot;">​</a></h3><p>Now repeat this action 5 times</p><p>This will:</p><ol><li>Trigger the blocker</li><li>Block every JSON with status code 423</li><li>Block every HTML request with status code 423 and show a view screen</li></ol><div class="info custom-block"><p class="custom-block-title">INFO</p><p>All these reponses to requests and blocks are completely customizable</p></div>`,21),l=[t];function p(i,r,c,d,h,y){return s(),e("div",null,l)}const A=a(n,[["render",p]]);export{u as __pageData,A as default};
